mapper.select:

	SELECT COUNT(*)
	FROM T_MEMBER
	WHERE EMAIL = #{email}

mapper.insert:

	INSERT INTO T_MEMBER(
	    EMAIL, NAME, PASSWORD, ENTER_DATE
	) VALUES(
	    #{email}, #{name}, #{password}, sysdate
	)


	UPDATE T_MEMBER SET
		NAME = #{name},
		PASSWORD = #{password}
	WHERE EMAIL = #{email}
